import React, { useEffect, useState } from 'react';
import OpenTickets from './OpenTickets';
import AssignedTickets from './AssignedTickets';

const HomeTickets: React.FC = () => {
    const [view, setView] = useState('open');  // This state will determine which ticket list to show

    return (
        <div>
            {/* Buttons to toggle between Open and Assigned tickets */}
            <button className={view == "open" ? 'buttonViewed' : 'buttonStyle'}  onClick={() => setView('open')}>Open Tickets</button>
            <button className={view == "assigned" ? 'buttonViewed' : 'buttonStyle'} onClick={() => setView('assigned')}>Assigned Tickets</button>

            {/* Conditionally render the OpenTickets or AssignedTickets based on the view state */}
            {view === 'open' && <OpenTickets />}
            {view === 'assigned' && <AssignedTickets />}
        </div>
    );
};

export default HomeTickets;
